open Batteries

let b64_data = ""

let rec build_ciphertext (bs : int BatEnum.t) : int list =
  match BatEnum.get bs with
  | None -> []
  | Some n -> n :: build_ciphertext bs

let ciphertext = b64_data
                 |> BatIO.input_string
                 |> Base64.decode
                 |> BatIO.bytes_of
                 |> build_ciphertext

let int_distance (n1 : int) (n2 : int) : int =
  let shifts = List.range 0 `To 7 in
  let diff = n1 lxor n2 in
  let reducer = fun shift sum -> sum + (diff lsr shift land 1) in
  List.fold_right reducer shifts 0

let distance (ns1 : int list) (ns2 : int list) : int =
  let pairs = List.combine ns1 ns2 in
  let distances = List.map (fun (n1, n2) -> int_distance n1 n2) pairs in
  List.fold_right (+) distances 0

let rec pairs (items : 'a list) : ('a * 'a) list =
  match items with
  | [] -> []
  | i :: is -> List.map (fun o -> (i, o)) is @ pairs is

let normalized_ciphertext_distance (keysize : int) : float =
  let word = fun o -> List.take keysize (List.drop (keysize * o) ciphertext) in
  let word_pairs = pairs (List.map word [0; 1; 2; 3]) in
  let distances = List.map (fun (w1, w2) -> distance w1 w2) word_pairs in
  let sum = List.fold_right (fun n s -> s +. (Float.of_int n)) distances 0.0 in
  sum /. (Float.of_int keysize) /. 6.0

let min_distance (ks1 : int) (ks2 : int) =
  let s1 = normalized_ciphertext_distance ks1 in
  let s2 = normalized_ciphertext_distance ks2 in
  if s1 < s2 then ks1 else ks2

let rec every_nth_element (ns : int list) (i : int) : int list =
  match ns with
  | [] -> []
  | n :: _ -> n :: every_nth_element (List.drop i ns) i

let build_byte_groups (ks : int) : int list list =
  let offsets = List.range 0 `To (ks - 1) in
  List.map (fun o -> every_nth_element (List.drop o ciphertext) ks) offsets

let rec count_spaces (nums : int list) (k : int) : int =
  match nums with
  | [] -> 0
  | n :: ns -> if n lxor k = 32
               then 1 + count_spaces ns k
               else count_spaces ns k

let find_key_byte (nums : int list) : int =
  let keys = List.range 0 `To 255 in
  let counts = List.map (fun k -> (k, count_spaces nums k)) keys in
  let compare_counts = fun (k1, c1) (k2, c2) -> if c1 > c2
                                                then (k1, c1)
                                                else (k2, c2) in
  List.fold_right compare_counts counts (0, 0) |> fst

let rec decrypt (nums : int list) (key : int Dllist.t) : int list =
  match nums with
  | [] -> []
  | n :: ns -> n lxor (Dllist.get key) :: decrypt ns (Dllist.next key)

let main () =
  let keysizes = List.range 2 `To 40 in
  let keysize = List.fold_right min_distance keysizes 50 in
  let groups = build_byte_groups keysize in
  let key = List.map find_key_byte groups |> Dllist.of_list in
  decrypt ciphertext key
  |> List.map Char.chr
  |> String.of_list
  |> print_string

let () = if Sys.argv.(0) = "./challenge6" then main () else ()
